import React, { Component } from 'react';

class App extends Component {
    constructor(props){
        super(props);

        this.state = {
            number: 0
        };
    }

    render() {
        return (
            <div>
                <h2>Button Click</h2>

                <p>The number on display should change based on the button clicked</p>
                <p>This small app would help me practice buttons and changing states</p>

                <div>
                    <h3 style={{"paddingLeft":"50%"}}>{this.state.number}</h3>
                    <button>+1</button> <button>0</button> <button>-1</button>
                </div>
            </div>
        );
    }
}

export default App;
